// @ts-nocheck
import { useRef, useEffect, useState } from "react"
import mapboxgl from "mapbox-gl"
import "mapbox-gl/dist/mapbox-gl.css"
import MapboxGeocoder from '@mapbox/mapbox-gl-geocoder'
import ReactDOM from 'react-dom'
import { makeStyles } from '@material-ui/core'
import clusterBlue from "../assets/clusterBlue.svg"
import unclusteredPoint from "../assets/unclusteredPoint.svg"
import "../styles/App.css"
import Legend from "../components/Legend"
import { mapboxStyle, states12MilesRadius, states12MilesSteps, states18MilesRadius, states18MilesSteps, states6MilesRadius, states6MilesSteps, statesHeatmapFill, statesHeatmapSteps, statesMapFilter, statesCircleStrokeWidth, miles6, miles12, miles18 } from "../helpers/Utils"


const useStyles = makeStyles(() => ({
  chartDiv: {
    height: '25em'
  }
}))

mapboxgl.accessToken =
  "pk.eyJ1IjoiZW5nbWFwYm94IiwiYSI6ImNqY3M1ZXA5czM2ZGYzM283ZmhscDltcGMifQ.Za7ut_7UThGzvjWhX4ACJQ";


export const Mapbox = ({ geoJson, poi, Popup }) => {
  const [colorScheme, setColorScheme] = useState<Array<string>>([])
  const [steps, setSteps] = useState<Array<number>>([])

  const [poiViews6Miles, setPoiViews6Miles] = useState<Array<number>>([])
  const [poiColor6Miles, setPoiColor6Miles] = useState<Array<string>>([])

  const [poiViews12Miles, setPoiViews12Miles] = useState<Array<number>>([])
  const [poiColor12Miles, setPoiColor12Miles] = useState<Array<string>>([])

  const [poiViews18Miles, setPoiViews18Miles] = useState<Array<number>>([])
  const [poiColor18Miles, setPoiColor18Miles] = useState<Array<string>>([])

  const mapContainer = useRef()
  const map = useRef()
  const popUpRef = useRef()
  const nav = useRef()
  const geoCoder = useRef()


  const classes = useStyles()


  useEffect(() => {
    setColorScheme(statesHeatmapFill)
    setSteps(statesHeatmapSteps)

    setPoiColor6Miles(states6MilesRadius)

    setPoiViews6Miles(states6MilesSteps)

    setPoiColor12Miles(states12MilesRadius)
    setPoiViews12Miles(states12MilesSteps)

    setPoiColor18Miles(states18MilesRadius)
    setPoiViews18Miles(states18MilesSteps)
  }, [])

  useEffect(() => {
    //initialize popup just once
    popUpRef.current = new mapboxgl.Popup({
        closeButton: false,
        closeOnClick: false,
    })
}, [])

useEffect(() => {
  nav.current = new mapboxgl.NavigationControl({
    showCompass: false,
    showZoom: true
  });


},  [])



  useEffect(() => {
    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: mapboxStyle,
      center: [-97, 36],
      zoom: 3,
    })

    // only want to work with the map after it has fully loaded
    // if you try to add sources and layers before the map has loaded
    // things will not work properly
    map.current.on("load", () => {
      map.current.addSource("sites", {
        type: "geojson",
        data: poi,
      })

      map.current.addSource("states", {
        type: "geojson",
        data: geoJson,
      })

      map.current.addLayer({
        id: "hover",
        type: "fill",
        source: "states",
        paint: {
          "fill-color": statesMapFilter,
          "fill-opacity": 0.8,
          "fill-outline-color": "#000",
        },

      })


      map.current.addLayer({
        id: "unclustered-point-1",
        type: "circle",
        source: "sites",
        filter: ["!", ["has", "point_count"]],
        paint: {
          "circle-opacity": 0,
          'circle-stroke-opacity': 0.5,
          "circle-radius": [
            "interpolate",
            ["linear"],
            ["zoom"],

            // zoom is 5 (or less) -> circle radius will be 1px
            5,
            5,
            // zoom is 10 (or greater) -> circle radius will be 5px
            6,
            12,

            7,
            24,

            8,
            36
          ],
          "circle-stroke-width": [
            "interpolate",
            ["linear"],
            ["zoom"],
            // zoom is 5 (or less) -> circle radius will be 1px
            5,
            3,
            // zoom is 10 (or greater) -> circle radius will be 5px
            6,
            11,

            7,
            22,

            8,
            36
          ],
          "circle-stroke-color": statesCircleStrokeWidth,
        },
      })
      map.current.addLayer({
        id: "unclustered-point",
        type: "circle",
        source: "sites",
        filter: ["!", ["has", "point_count"]],
        paint: {
          "circle-color": [
            "case", // Begin case expression
            ["<", ["get", "sixMilesViews"], poiViews6Miles[0]],
            poiColor6Miles[0],
            [
              "all",
              [">=", ["get", "sixMilesViews"], poiViews6Miles[0]],
              ["<", ["get", "sixMilesViews"], poiViews6Miles[1]],
            ],
            poiColor6Miles[1],
            [
              "all",
              [">=", ["get", "sixMilesViews"], poiViews6Miles[1]],
              ["<", ["get", "sixMilesViews"], poiViews6Miles[2]],
            ],
            poiColor6Miles[2],
            [
              "all",
              [">=", ["get", "sixMilesViews"], poiViews6Miles[2]],
              ["<", ["get", "sixMilesViews"], poiViews6Miles[3]],
            ],
            poiColor6Miles[3],
            [
              "all",
              [">=", ["get", "sixMilesViews"], poiViews6Miles[3]],
              ["<", ["get", "sixMilesViews"], poiViews6Miles[4]],
            ],
            poiColor6Miles[4],
            [">=", ["get", "sixMilesViews"], poiViews6Miles[4]],
            poiColor6Miles[5],
            "#001814",
          ],
          "circle-radius": [
            "interpolate",
            ["linear"],
            ["zoom"],
            // zoom is 5 (or less) -> circle radius will be 1px
            5,
            3,
            // zoom is 10 (or greater) -> circle radius will be 5px
            6,
            20,
          ],
          "circle-stroke-width": [
            "interpolate",
            ["linear"],
            ["zoom"],
            // zoom is 5 (or less) -> circle radius will be 1px
            5,
            1,
            // zoom is 10 (or greater) -> circle radius will be 5px
            6,
            8,
          ],
          "circle-stroke-color": [
            "case", // Begin case expression
            ["<", ["get", "twelveMilesViews"], poiViews12Miles[0]],
            poiColor12Miles[0],
            [
              "all",
              [">=", ["get", "twelveMilesViews"], poiViews12Miles[0]],
              ["<", ["get", "twelveMilesViews"], poiViews12Miles[1]],
            ],
            poiColor12Miles[1],
            [
              "all",
              [">=", ["get", "twelveMilesViews"], poiViews12Miles[1]],
              ["<", ["get", "twelveMilesViews"], poiViews12Miles[2]],
            ],
            poiColor12Miles[2],
            [
              "all",
              [">=", ["get", "twelveMilesViews"], poiViews12Miles[2]],
              ["<", ["get", "twelveMilesViews"], poiViews12Miles[3]],
            ],
            poiColor12Miles[3],
            [
              "all",
              [">=", ["get", "twelveMilesViews"], poiViews12Miles[3]],
              ["<", ["get", "twelveMilesViews"], poiViews12Miles[4]],
            ],
            poiColor12Miles[4],
            [">=", ["get", "twelveMilesViews"], poiViews12Miles[4]],
            poiColor12Miles[5],
            "#00c3a2",
          ],
        },
      })

      map.current.on("click", "unclustered-point", (e: any) => {
        let f = map.current.queryRenderedFeatures(e.point, { layers: ['unclustered-point-1'] })

        if (f.length) {
            map.current.getCanvas().style.cursor = 'pointer'
            let popupNode = document.createElement('div')
            if (Popup) {
                let elem = <Popup data={f[0]} />
                ReactDOM.render(elem, popupNode)
                popUpRef.current
                    .setLngLat(e.lngLat)
                    .setDOMContent(popupNode)
                    .addTo(map.current)
            }
            return
        }
      })

        map.current.on('mouseleave', `hover`, () => {
        map.current.getCanvas().style.cursor = ''
        popUpRef.current.remove()
      }) 

      map.current.addControl(nav.current)


    })

    // cleanup function to remove map on unmount
    return () => map.current.remove()
  }, [Popup, geoJson, poi, poiColor12Miles, poiColor6Miles, poiViews12Miles, poiViews6Miles])

  return (
    <>
      <Legend
        type="dots"
        clusters="Clusters"
        pointCount="Views"
        clusterImg={clusterBlue}
        unclusteredImg={unclusteredPoint}
        unclusteredPoint="Unclustered Points"
        colorScheme={colorScheme}
        steps={steps}
        poi="POI"
        views="Views"
        poiColor6Miles={poiColor6Miles}
        poiViews6Miles={poiViews6Miles}
        poiColor12Miles={poiColor12Miles}
        poiViews12Miles={poiViews12Miles}
        poiColor18Miles={poiColor18Miles}
        poiViews18Miles={poiViews18Miles}
      />
      <div ref={mapContainer} style={{ width: "100%", height: "100vh" }} />
    </>
  )
}
